// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract PositionToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor(string memory name_, string memory symbol_, address admin) ERC20(name_, symbol_) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MINTER_ROLE, admin);
        _grantRole(BURNER_ROLE, admin);
    }

    function decimals() public pure override returns (uint8) { return 18; }

    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyRole(BURNER_ROLE) {
        _burn(from, amount);
    }
}

contracts/src/SpeculateCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./PositionToken.sol";
import "./interfaces/AggregatorV3Interface.sol";

contract SpeculateCore is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant MARKET_CREATOR_ROLE = keccak256("MARKET_CREATOR_ROLE");
    uint256 private constant USDC_TO_E18 = 1e12;
    uint256 public constant MAX_USDC_PER_TRADE = 100_000e6;

    enum MarketStatus { Active, Paused, Resolved }
    enum OracleType { None, ChainlinkFeed, ChainlinkFunctions }
    enum Comparison { Above, Below, Equals }

    struct ResolutionConfig {
        uint256 expiryTimestamp;
        OracleType oracleType;
        address oracleAddress;
        bytes32 priceFeedId;
        uint256 targetValue;
        Comparison comparison;
        bool yesWins;
        bool isResolved;
    }

    struct Market {
        PositionToken yes;
        PositionToken no;
        uint256 qYes;
        uint256 qNo;
        uint256 usdcVault;
        uint256 totalPairsUSDC;
        uint256 bE18;
        uint16 feeTreasuryBps;
        uint16 feeVaultBps;
        uint16 feeLpBps;
        uint16 maxTradeBps;
        MarketStatus status;
        bool exists;
        bool sellFees;
        string question;
        address lp;
        ResolutionConfig resolution;
    }

    IERC20 public immutable usdc;
    address public treasury;
    mapping(uint256 => Market) public markets;
    uint256 public marketCount;
    address public chainlinkResolver;

    uint256 private constant SCALE = 1_000_000_000_000_000_000;
    uint256 private constant LN2 = 693_147_180_559_945_309;
    uint256 private constant LOG2_E = 1_442_695_040_888_963_407;

    event MarketCreated(uint256 indexed id, address yes, address no, string question, uint256 usdcVault, uint256 expiryTimestamp);
    event MarketResolved(uint256 indexed id, bool yesWins, uint256 resolvedPrice);
    event Redeemed(uint256 indexed id, address indexed user, bool isYes, uint256 tokensIn, uint256 usdcOut);
    event BuyYes(uint256 indexed id, address indexed user, uint256 usdcIn, uint256 tokensOut, uint256 avgPriceE6);
    event BuyNo(uint256 indexed id, address indexed user, uint256 usdcIn, uint256 tokensOut, uint256 avgPriceE6);
    event SellYes(uint256 indexed id, address indexed user, uint256 tokensIn, uint256 usdcOut, uint256 avgPriceE6);
    event SellNo(uint256 indexed id, address indexed user, uint256 tokensIn, uint256 usdcOut, uint256 avgPriceE6);
    event FeeCollected(uint256 indexed id, address indexed trader, uint256 feeT, uint256 feeV, uint256 feeL, bool isBuy);

    constructor(address _usdc, address _treasury) {
        require(_usdc != address(0) && _treasury != address(0), "zero");
        usdc = IERC20(_usdc);
        treasury = _treasury;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MARKET_CREATOR_ROLE, msg.sender);
    }

    // === MATH ===
    function mul(uint256 x, uint256 y) internal pure returns (uint256) { return x * y / SCALE; }
    function div(uint256 x, uint256 y) internal pure returns (uint256) { return x * SCALE / y; }

    function exp(uint256 x) internal pure returns (uint256) {
        if (x > 88 * SCALE) revert("exp");
        return exp2(mul(x, LOG2_E));
    }

    function exp2(uint256 x) internal pure returns (uint256) {
        if (x > 192 * SCALE) revert("exp2");
        uint256 intPart = x / SCALE;
        if (intPart > 255) revert("int");
        uint256 frac = x % SCALE;
        uint256 res = SCALE;
        uint256 term = SCALE;
        uint256 y = mul(frac, LN2);
        for (uint8 i = 1; i <= 20;) {
            term = mul(term, y) / i;
            res += term;
            unchecked { ++i; }
        }
        return (1 << intPart) * res;
    }

    function ln(uint256 x) internal pure returns (uint256) {
        if (x == 0) revert("ln0");
        return mul(log2(x), LN2);
    }

    function log2(uint256 x) internal pure returns (uint256) {
        uint256 res = 0;
        if (x >= SCALE << 128) { x >>= 128; res += 128 * SCALE; }
        if (x >= SCALE << 64)  { x >>= 64;  res += 64 * SCALE; }
        if (x >= SCALE << 32)  { x >>= 32;  res += 32 * SCALE; }
        if (x >= SCALE << 16)  { x >>= 16;  res += 16 * SCALE; }
        if (x >= SCALE << 8)   { x >>= 8;   res += 8 * SCALE; }
        if (x >= SCALE << 4)   { x >>= 4;   res += 4 * SCALE; }
        if (x >= SCALE << 2)   { x >>= 2;   res += 2 * SCALE; }
        if (x >= SCALE << 1)   { res += SCALE; x >>= 1; }
        uint256 z = div((x - SCALE), (x + SCALE));
        uint256 z2 = mul(z, z);
        uint256 w = SCALE;
        w += mul(z2, SCALE) / 3;
        w += mul(mul(z2, z2), SCALE) / 5;
        return res + mul(z, w);
    }

    function _C(uint256 qY, uint256 qN, uint256 b) internal pure returns (uint256) {
        uint256 eY = exp(div(qY, b));
        uint256 eN = exp(div(qN, b));
        return mul(b, ln(eY + eN));
    }

    function spotPriceYesE18(uint256 id) public view returns (uint256) {
        Market storage m = markets[id];
        if (!m.exists) revert("no");
        uint256 eY = exp(div(m.qYes, m.bE18));
        uint256 eN = exp(div(m.qNo, m.bE18));
        return div(eY, eY + eN);
    }

    function spotPriceYesE6(uint256 id) external view returns (uint256) {
        return spotPriceYesE18(id) / 1e12;
    }

    function spotPriceNoE6(uint256 id) external view returns (uint256) {
        uint256 yesE6 = spotPriceYesE18(id) / 1e12;
        return 1_000_000 - yesE6;
    }

    function getPriceYes(uint256 id) public view returns (uint256) {
        return spotPriceYesE18(id) / 1e12;
    }

    function getPriceNo(uint256 id) public view returns (uint256) {
        return 1_000_000 - getPriceYes(id);
    }

    // === ADMIN ===
    function setTreasury(address a) external onlyRole(DEFAULT_ADMIN_ROLE) { treasury = a; }
    function setChainlinkResolver(address a) external onlyRole(DEFAULT_ADMIN_ROLE) { chainlinkResolver = a; }
    function setAllFees(uint256 id, uint16 t, uint16 v, uint16 l, bool sf) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Market storage m = markets[id];
        require(m.exists && t + v + l <= 1000);
        m.feeTreasuryBps = t; m.feeVaultBps = v; m.feeLpBps = l; m.sellFees = sf;
    }
    function grantMarketCreator(address a) external onlyRole(DEFAULT_ADMIN_ROLE) { _grantRole(MARKET_CREATOR_ROLE, a); }

    // === MARKET CREATION ===
    function createMarket(
        string memory question,
        string memory yesName, string memory yesSymbol,
        string memory noName, string memory noSymbol,
        uint256, // initReserveE18 unused
        uint16 feeBps,
        uint16 maxTradeBps,
        uint256 initUsdc,
        uint256 expiryTimestamp,
        OracleType oracleType,
        address oracleAddress,
        bytes32 priceFeedId,
        uint256 targetValue,
        Comparison comparison
    ) external onlyRole(MARKET_CREATOR_ROLE) returns (uint256 id) {
        require(initUsdc >= 1e6 && feeBps <= 1000 && maxTradeBps <= 10000);
        id = ++marketCount;
        PositionToken yes = new PositionToken(yesName, yesSymbol, address(this));
        PositionToken no = new PositionToken(noName, noSymbol, address(this));
        yes.grantRole(yes.MINTER_ROLE(), address(this));
        yes.grantRole(yes.BURNER_ROLE(), address(this));
        no.grantRole(no.MINTER_ROLE(), address(this));
        no.grantRole(no.BURNER_ROLE(), address(this));

        Market storage m = markets[id];
        m.yes = yes; m.no = no;
        uint16 t = uint16(feeBps * 50 / 100);
        uint16 v = uint16(feeBps * 25 / 100);
        m.feeTreasuryBps = t;
        m.feeVaultBps = v;
        m.feeLpBps = feeBps - t - v;
        m.maxTradeBps = maxTradeBps;
        m.status = MarketStatus.Active;
        m.exists = true;
        m.lp = msg.sender;
        m.question = question;

        m.resolution = ResolutionConfig({
            expiryTimestamp: expiryTimestamp,
            oracleType: oracleType,
            oracleAddress: oracleAddress,
            priceFeedId: priceFeedId,
            targetValue: targetValue,
            comparison: comparison,
            yesWins: false,
            isResolved: false
        });

        usdc.safeTransferFrom(msg.sender, address(this), initUsdc);
        m.usdcVault = initUsdc;
        m.totalPairsUSDC = initUsdc;

        // b (E18) ~= initUsdc / ln(2) in share units
        m.bE18 = (initUsdc * USDC_TO_E18 * SCALE) / LN2;

        emit MarketCreated(id, address(yes), address(no), question, initUsdc, expiryTimestamp);
    }

    // === TRADING ===
    function findDelta(uint256 qS, uint256 qO, uint256 costE18, uint256 b) internal pure returns (uint256 delta) {
        if (costE18 == 0) return 0;
        uint256 lo = 0;
        uint256 hi = b * 80;
        uint256 baseCost = _C(qS, qO, b);
        for (uint256 i = 0; i < 60;) {
            uint256 mid = (lo + hi) / 2;
            uint256 newCost = _C(qS + mid, qO, b);
            if (newCost <= baseCost) {
                hi = mid;
            } else {
                uint256 diff = newCost - baseCost;
                if (diff < costE18) {
                    lo = mid;
                } else {
                    hi = mid;
                }
            }
            unchecked { ++i; }
        }
        delta = (lo + hi) / 2;
    }

    function _buy(uint256 id, bool isYes, uint256 usdcIn) internal returns (uint256 tokensOut, uint256 avgPriceE6) {
        Market storage m = markets[id];
        require(m.exists && m.status == MarketStatus.Active && usdcIn <= MAX_USDC_PER_TRADE);
        uint256 feeT = usdcIn * m.feeTreasuryBps / 10000;
        uint256 feeV = usdcIn * m.feeVaultBps / 10000;
        uint256 feeL = usdcIn * m.feeLpBps / 10000;
        uint256 net = usdcIn - feeT - feeV - feeL;
        uint256 netE18 = net * USDC_TO_E18;

        usdc.safeTransferFrom(msg.sender, address(this), usdcIn);
        if (feeT > 0) usdc.safeTransfer(treasury, feeT);
        if (feeL > 0 && m.lp != address(0)) usdc.safeTransfer(m.lp, feeL);
        m.usdcVault += net + feeV;
        m.totalPairsUSDC += net + feeV;
        emit FeeCollected(id, msg.sender, feeT, feeV, feeL, true);

        uint256 qS = isYes ? m.qYes : m.qNo;
        uint256 qO = isYes ? m.qNo : m.qYes;
        tokensOut = findDelta(qS, qO, netE18, m.bE18);
        require(tokensOut > 0);

        if (isYes) { m.yes.mint(msg.sender, tokensOut); m.qYes += tokensOut; }
        else { m.no.mint(msg.sender, tokensOut); m.qNo += tokensOut; }

        avgPriceE6 = usdcIn * 1e18 / tokensOut / 1e12;
    }

    function _sell(uint256 id, bool isYes, uint256 tokensIn) internal returns (uint256 usdcOut, uint256 avgPriceE6) {
        Market storage m = markets[id];
        require(m.exists && m.status == MarketStatus.Active && tokensIn > 0);
        uint256 qS = isYes ? m.qYes : m.qNo;
        uint256 qO = isYes ? m.qNo : m.qYes;
        require(tokensIn <= qS);

        uint256 oldC = _C(qS, qO, m.bE18);
        uint256 newC = _C(qS - tokensIn, qO, m.bE18);
        uint256 refundE18 = oldC - newC;
        uint256 gross = refundE18 / USDC_TO_E18;
        require(gross <= m.usdcVault);

        if (isYes) {
            IERC20(address(m.yes)).safeTransferFrom(msg.sender, address(this), tokensIn);
            m.yes.burn(address(this), tokensIn);
            m.qYes -= tokensIn;
        } else {
            IERC20(address(m.no)).safeTransferFrom(msg.sender, address(this), tokensIn);
            m.no.burn(address(this), tokensIn);
            m.qNo -= tokensIn;
        }

        if (m.sellFees) {
            uint256 fT = gross * m.feeTreasuryBps / 10000;
            uint256 fV = gross * m.feeVaultBps / 10000;
            uint256 fL = gross * m.feeLpBps / 10000;
            usdcOut = gross - fT - fV - fL;
            if (fT > 0) usdc.safeTransfer(treasury, fT);
            if (fL > 0 && m.lp != address(0)) usdc.safeTransfer(m.lp, fL);
            emit FeeCollected(id, msg.sender, fT, fV, fL, false);
            m.usdcVault -= usdcOut + fV;
            m.totalPairsUSDC -= usdcOut + fV;
        } else {
            usdcOut = gross;
            m.usdcVault -= gross;
            m.totalPairsUSDC -= gross;
        }

        usdc.safeTransfer(msg.sender, usdcOut);
        avgPriceE6 = usdcOut * 1e18 / tokensIn / 1e12;
    }

    // === PUBLIC ===
    function buyYes(uint256 id, uint256 usdcIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 p) = _buy(id, true, usdcIn);
        require(out >= minOut, "slippage");
        emit BuyYes(id, msg.sender, usdcIn, out, p);
    }

    function buyNo(uint256 id, uint256 usdcIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 p) = _buy(id, false, usdcIn);
        require(out >= minOut, "slippage");
        emit BuyNo(id, msg.sender, usdcIn, out, p);
    }

    function sellYes(uint256 id, uint256 tokensIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 p) = _sell(id, true, tokensIn);
        require(out >= minOut, "slippage");
        emit SellYes(id, msg.sender, tokensIn, out, p);
    }

    function sellNo(uint256 id, uint256 tokensIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 p) = _sell(id, false, tokensIn);
        require(out >= minOut, "slippage");
        emit SellNo(id, msg.sender, tokensIn, out, p);
    }

    // === RESOLUTION & REDEEM ===
    function resolveMarket(uint256 id, bool yesWins) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Market storage m = markets[id];
        require(m.exists && !m.resolution.isResolved);
        m.status = MarketStatus.Resolved;
        m.resolution.yesWins = yesWins;
        m.resolution.isResolved = true;
        emit MarketResolved(id, yesWins, 0);
    }

    function resolveMarketWithPrice(uint256 id, uint256 price) external {
        require(msg.sender == chainlinkResolver || hasRole(DEFAULT_ADMIN_ROLE, msg.sender));
        Market storage m = markets[id];
        require(m.exists && !m.resolution.isResolved && block.timestamp >= m.resolution.expiryTimestamp);
        bool yesWins = price > m.resolution.targetValue ?
            m.resolution.comparison == Comparison.Above :
            price < m.resolution.targetValue ?
                m.resolution.comparison == Comparison.Below :
                m.resolution.comparison == Comparison.Equals;
        m.status = MarketStatus.Resolved;
        m.resolution.yesWins = yesWins;
        m.resolution.isResolved = true;
        emit MarketResolved(id, yesWins, price);
    }

    function checkUpkeep(uint256 id) external view returns (bool, bytes memory) {
        Market storage m = markets[id];
        bool need = m.exists && m.status == MarketStatus.Active && !m.resolution.isResolved &&
                    block.timestamp >= m.resolution.expiryTimestamp && m.resolution.oracleType != OracleType.None;
        return (need, abi.encode(id));
    }

    function getMarketResolution(uint256 id) external view returns (ResolutionConfig memory) {
        return markets[id].resolution;
    }

    function redeem(uint256 id, bool isYes, uint256 tokensIn) external nonReentrant {
        Market storage m = markets[id];
        require(m.exists && m.resolution.isResolved && tokensIn > 0 && isYes == m.resolution.yesWins);

        if (isYes) {
            IERC20(address(m.yes)).safeTransferFrom(msg.sender, address(this), tokensIn);
            m.yes.burn(address(this), tokensIn);
        } else {
            IERC20(address(m.no)).safeTransferFrom(msg.sender, address(this), tokensIn);
            m.no.burn(address(this), tokensIn);
        }

        uint256 usdcOut = tokensIn / 1e12;
        require(usdcOut > 0 && usdcOut <= m.usdcVault);
        m.usdcVault -= usdcOut;
        m.totalPairsUSDC -= usdcOut;
        usdc.safeTransfer(msg.sender, usdcOut);
        emit Redeemed(id, msg.sender, isYes, tokensIn, usdcOut);
    }
}

contracts/src/Treasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Treasury is Ownable {
    event Withdraw(address indexed token, address indexed to, uint256 amount);

    constructor(address owner_) Ownable(owner_) {}

    function withdraw(address token, address to, uint256 amount) external onlyOwner {
        require(IERC20(token).transfer(to, amount), "xfer");
        emit Withdraw(token, to, amount);
    }
}

